import Layout from "../components/Layout";
import axios from 'axios'

class Addbook extends React.Component{
    constructor(){
        super();

        this.state = {
            book_id :'',
            name:'',
            author: '',

        }
        
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };
   
    handleChange = (e) => {
        this.setState({
            [e.target.book_id]:e.target.value,
          [e.target.name]: e.target.value,
          [e.target.author]:e.target.author
        })
      }
      handleSubmit(event) {
        event.preventDefault();

        const data = { book_id:this.state.book_id,name:this.state.name , author:this.state.author}
        axios.post('http://localhost:3001/api/books',{
            book_id:data.book_id,
            name:data.name,
            author:data.author
        })
        
        .then(res =>{
            console.log(res);
          
    

        })
        .catch(err => console.error(err));}

        render()
        {
            return(
                <Layout>
        <form onSubmit={this.handleSubmit}>
        <table>
            <tr>
                <td>Book Id:</td>
                <td><input type="text" name="book_id" id="book_id" /></td>
            </tr>
            <tr>
                <td>Book Name:</td>
                <td><input type="text" name="name" id="name"/></td>
            </tr>
            <tr>
                <td>Author:</td>
                <td><input type="text" name="author"id="author"/></td>
            </tr>
            <tr>
                
                <td><input type="submit" value="Submit"/></td>
                <td><input type="reset" value="Reset"/></td>
            </tr>
        </table>
    </form>
    </Layout>
            )
        }
       



}
export default Addbook
