import axios from 'axios'
import Layout from '../components/Layout'
import useSWR from 'swr';
import {useState,useEffect} from 'react';

export default function Listbook(){
    const fetcher = url => axios.get(url).then(res => res.data.response)
    const { data, error } = useSWR('http://localhost:3001/api/books', fetcher)
    console.log(data)

   
    return(
        <div>
            <Layout>
                <h2>List of all books</h2>
                <table>
                    <thead>
                        <tr>
                            <td>bookid</td>
                            <td>name</td>
                            <td>author</td>
                        </tr>
                    </thead>
                    <tbody>
                    {data?.map((book => <tr key={book.book_id}><td>{book.book_id}</td></tr>))}


                    </tbody>
                </table>
        <ol>
        {data?.map((book => <li key={book.book_id}>{book.name}</li>
            ))}
    </ol>
    </Layout>
    </div>
    )

}
